sourcesContent:
import mongoose from 'mongoose';

const Schema = mongoose.Schema
const hotelSchema = new Schema({
    name:{
        type: String,
        required: true
    },
    type:{
        type: String,
        required: true
    },
    city:{
        type: String,
        required: true
    },
    address:{
        type: String,
        required: true
    },
    distance:{
        type: String,
        required: true
    },
    photos:{
        type:[String],
    },
    title:{
         type:String,
         required: true
    }, 
    description:{
        type: String,
        required: true
    },
    rating:{
        type: Number,
        min:0,
        max:5
    },
    rooms:{
        type:[String],
    },
    cheapestPrice:{
        type:Number,
        required: true
    },
    featured:{
        type:Boolean,
        default:false
    }
})

export default mongoose.model('hotel', hotelSchema);
